package learn;
/**
 * Math方法
 * 
 * 
 * */
public class day3_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Math.PI);
		System.out.println(Math.abs(-10));  //取绝对值
		
		System.out.println(Math.ceil(12.3));		//向上取整 double
		System.out.println(Math.ceil(12.99));
		
		System.out.println(Math.floor(12.3));	//向下取整		double
		System.out.println(Math.floor(12.99));
		
		System.out.println(Math.max(20, 30));
		
		System.out.println(Math.min(10, 20));
		
		System.out.println(Math.pow(2, 3));	//前面为底数,后面为指数 8.0 double
		
		System.out.println( Math.random());  //生成0到1之间的任意数包括0单不包括1
		
		System.out.println(Math.round(12.3));  //四舍五入
		System.out.println(Math.round(12.5));
		
		System.out.println(Math.sqrt(4));  //开平方
		
		
		
	}

}
